const isMobile = window.innerWidth < 768;

AOS.init({
  duration: 500, // Длительность анимации в миллисекундах (0.5 секунды)
  once: true,    // Анимация запускается только один раз
  disable: 'mobile' // Отключаем анимацию на мобильных устройствах
  
});


const burgerIcon = document.getElementById('burger-icon');
const navMenu = document.getElementById('nav-menu');
const overlay = document.getElementById('overlay');

// Открытие и закрытие меню
burgerIcon.addEventListener('click', () => {
    navMenu.classList.toggle('open');
    burgerIcon.classList.toggle('open');
});

// Закрытие меню при клике на затемнение
overlay.addEventListener('click', () => {
    navMenu.classList.remove('open');
    burgerIcon.classList.remove('open');
});
